import React, {Component} from 'react';
import './App.css';
import Main from './components/Main-container';
import ImageSlider from './components/Slider';
import ArticleContainer from './components/Article-container';
import SimpleSlider from './components/Slider2';
import SSlider from './components/Slider3'
import { Link } from 'react-router-dom';
import SearchBox1 from './components/SearchBox1'
import SearchBox from './components/SearchBox'
import { Route } from 'react-router-dom'


class Home extends Component{

  constructor(){
    super();
    this.state={
      books:[],
      searchField:''
  
    }

  }

  componentDidMount(){
    fetch('./data.json')
    .then(response=>response.json())
    .then(user =>this.setState({books: user}));
  }

  handleChange =(e) => this.setState({searchField: e.target.value})

  render(){

    const {books, searchField} = this.state;
    const filteredMonsters = books.filter(books =>
      books.title.toLowerCase().includes(searchField.toLowerCase()));

 return (
    <>



          
          <Route render={({ history }) => <SearchBox history={history} />} />
           
    
    <SearchBox1
          placeholder='Search Titles/Authors'
          handleChange={this.handleChange}
        books = {filteredMonsters} 

          
        />
        <Main></Main>
        <div className='gg'>
        <div className='nav-item'>

              </div>
        <ImageSlider>
        {this.state.books.map(book=><p key={book.id}>{book.title} </p>)}
        </ImageSlider> </div>
       <ArticleContainer></ArticleContainer>
       <div className='gg'>

     <SimpleSlider> 
       {this.state.books.map(book=><p key={book.id}>{book.title} </p>)}
       </SimpleSlider>
       </div>
       <div className='ggg'>
       <SSlider> 
       {this.state.books.map(book=><p key={book.id}>{book.title} </p>)}
       </SSlider>
       </div>

      
     
    </>
  );
}}

export default Home;
